// Route definition
type Route struct {
	Permissions      []permission.Permission
	Method           string
	Methods          []string // Bind many methods for the same route
	IsPrefix         bool     // Use the path as prefix
	Path             string
	EnableTracing    bool
	TracingEventName string
}

